mapper.insert:
INSERT INTO b_variation_items
    (
     user_id
     ,trade_cd
     , title
     , item_id
     , order_sq
     , reg_dt
     , reg_id
     )
VALUES
    (
     #{user_id}
     , #{trade_cd}
     , #{title}
     , #{item_id}
     , 0
     , now()
     ,#{user_id}
    )

mapper.update:
update  b_variation_items set
    title = #{title}
    ,trade_cd = #{trade_cd}
where seq = #{seq}

mapper.select:
set @dno = (select (item_no+1) from d_no where user_id = #{user_id});
update d_no set
    item_no = @dno
    where user_id = #{user_id};
select @dno as item_no;

select seq as seq ,user_id as user_id ,trade_cd as trade_cd ,title as title ,item_id as item_id ,DATE_FORMAT(reg_dt, '%Y-%m-%d') as reg_dt , 'N' as fix_gb from b_variation_items where 1=1
and trade_cd = #{s_search_gb}
and trade_cd = #{s_search_gb}
and title = #{s_search_txt}
union all select seq as seq ,'' as user_id ,trade_cd as trade_cd ,title as title ,item_id as item_id ,'' as reg_dt ,'Y' as fix_gb from b_fixed_items where 1=1
and trade_cd = #{s_search_gb}
and trade_cd = #{s_search_gb}
and title = #{s_search_txt}
